def translate(c):
    total = c
    for d in total:
        if d['Dis'] not in disease.keys():
            d['Dis'] = ["Unknown", "N/A", d["Dis"]]
        else:
            d['Dis'] = disease[d['Dis']]+[d['Dis']]
        newSym = []
        for symptom in d['Sym']:
            if symptom not in symlabels.keys():
                continue
            else:
                newSym.append(symlabels[symptom])
        d['Sym'] = newSym
        blank = []
        for symptom in d['Input']:
            if symptom not in symlabels.keys():
                continue
            else:
                blank.append(symlabels[symptom])
        d['Input'] = blank
    return total

